document.querySelectorAll(".desktop").forEach((e=>{e.addEventListener("click",(function(t){t.preventDefault(),e.blur();document.querySelector(".header").classList.remove("scroll");const o=e.getAttribute("href").slice(1),c=document.getElementById(o);if(c){const e=window.scrollY,t=c.getBoundingClientRect().top+e-30;window.scrollTo({top:t,behavior:"smooth"})}}))}));